export const colorIcon = (
  icon: string,
  fillColor: string,
  strokeColor: string
) => {
  if (icon) {
    const coloredIcon = icon
      .replace("FILLCOLOR", fillColor)
      .replace(/STROKECOLOR/g, strokeColor);
    return coloredIcon;
  }
};
